#include <iostream>#include <vector>const int INF = 1e9 + 7;void print(std::vector<int> readVec){    int sz = readVec.size();    for(int i = 0; i < sz; ++ i){        std::cout << readVec.at(i) << std::endl;    }}int main(){    int n;    std::string str;    std::cin >> n >> str;    int east = 0, west = 0;    std::vector<int> eastgazers(n, 0), westgazers(n, 0);    for(int i = 0; i < n - 1; ++i){        if('W' == str.at(i)){            ++west;        }        westgazers.at(i + 1) = west;        if('E' == str.at(n - 1 - i)){            ++east;        }        eastgazers.at(n - 1 - i) = east;        print(westgazers);        std::cout << "------" << std::endl;        print(eastgazers);    }        int min = INF;    for(int i = 0; i < n; ++i){        min = std::min(min, eastgazers.at(i) + westgazers.at(i));    }    std::cout << min << std::endl;    return 0;}