#include <iostream>int main(){    std::string s;    std::cin >> s;    long long k;    std::cin >> k;    int sz = s.size();    char ans = '1';    int i = 0;    while(i < sz){        if('1' != s.at(i)){            ans = s.at(i);            break;        }        ++i;    }    if(k <= i){        ans = '1';    }    std::cout << ans << std::endl;    return 0;}