#include <iostream>#include <vector>void printVec(std::vector<int> readVec){    int n = readVec.size();    for(int i = 0; i < n;++i){        std::cout << readVec.at(i);        if(n - 1 != i){            std::cout << " ";        }else{            std::cout << std::endl;        }    }}int main(){    int n, k;    std::cin >> n >> k;    int limit = 2 * n / k;    std::vector<std::vector<int>> list;    for(int x = 1; x <= limit; ++x){        for(int y = 1; y <= limit; ++y){            for(int z = 1; z <= limit; ++z){                int multiSum = k * (x + y + z);                if(0 == multiSum % 2){                    int sum = multiSum / 2, a = sum - k * y, b = sum - k * z, c = sum - k * x;                    if(0 < a && a <= n && 0 < b && b <= n && 0 < c && c <= n ){                        list.push_back(std::vector<int>({a, b, c}));//                        printVec(*(list.end() - 1));                    }                }            }        }    }    std::cout << list.size() << std::endl;    return 0;}