#include <iostream>#include <vector>#include <cmath>long long gcd(long long a, long long b){    long long num = std::max(a, b), denom = a + b - num, buf;    while(0 != denom){        buf = denom;        denom = num % denom;        num = buf;    }    return num;}int main(){    int n, val;    std::cin >> n >> val;    std::vector<int> x = {val};    for(int i = 0; i < n; ++i){        std::cin >> val;        x.push_back(val);    }    std::sort(x.begin(), x.end());    std::vector<int> spans(n);    int ans;    if(2 == x.size()){        ans = x.at(1) - x.at(0);    }else{        ans = gcd(x.at(1) - x.at(0), x.at(2) - x.at(1));        for(int i = 2; i < n; ++i){            ans = gcd(ans, x.at(i + 1) - x.at(i));        }    }    std::cout << ans << std::endl;    return 0;}