#include <iostream>#include <vector>int main(){    std::string s, t;    std::cin >> s >> t;    int n = s.size(), i = 0;    std::vector<int> sList(26, -1), tList(26, -1);    bool sFlg = true, tFlg = true;     while(sFlg && tFlg && i < n){        int sElem = s.at(i) - 97;        if(sList.at(sElem) < 0){            sList.at(sElem) = t.at(i) - 97;        }else{            sFlg = sList.at(sElem) == t.at(i) - 97;        }        int tElem = t.at(i) - 97;        if(tList.at(tElem) < 0){            tList.at(tElem) = s.at(i) - 97;        }else{            tFlg = tList.at(tElem) == s.at(i) - 97;        }        ++i;    }    std::string ans = "No";    if(sFlg && tFlg){        ans = "Yes";    }    std::cout << ans << std::endl;    return 0;}