#include <algorithm>#include <iostream>#include <iomanip>#include <sstream>#include <vector>int main(){    int n, m;    std::cin >> n >> m;    std::vector<std::pair<long long, long long>> order(m);    std::vector<std::vector<long long>> record(n);    for(int i = 0; i < m; ++i){        long long p, y;        std::cin >> order.at(i).first >> order.at(i).second;        record.at(order.at(i).first - 1).push_back(order.at(i).second);    }    for(int i = 0; i < n; ++i){        auto start = record.at(i).begin();        auto end = record.at(i).end();        std::sort(start, end);    }    for(int i = 0; i < m; ++i){        std::stringstream ss;        long long val = order.at(i).first;        ss << std::setw(6) << std::setfill('0') << val;        auto start = record.at(val - 1).begin();        long long dist = std::distance(start, std::lower_bound(start, record.at(val - 1).end(), order.at(i).second));        ss << std::setw(6) << dist + 1;        std::cout << ss.str() << std::endl;    }    return 0;}