#include <iostream>#include <algorithm>#include <vector>int main(){    int n, k;    std::cin >> n >> k;    std::vector<int> h(n);    for(int i = 0; i < n; ++i){        std::cin >> h.at(i);    }    std::sort(h.begin(), h.end());    int ans = 2147483647;    for(int i = 0; i < n - k + 1; ++i){        ans = std::min(ans, h.at(i + k - 1) - h.at(i));    }    std::cout << ans << std::endl;    return 0;}