#include <iostream>#include <vector>int main(){    int n, m;    std::cin >> n >> m;    std::vector<int> x(m);    for(int i = 0; i < m; ++i){        std::cin >> x.at(i);    }    std::vector<int> y(m - 1);    for(int i = 0; i < m - 1; ++i){        y.at(i) = x.at(i + 1) - x.at(i);    }}