#include <iostream>#include <vector>int main(){    int n, m;    std::cin >> n >> m;    std::vector<int> x(m);    for(int i = 0; i < m; ++i){        std::cin >> x.at(i);    }    std::sort(x.begin(), x.end());    std::vector<int> y(m - 1);    for(int i = 0; i < m - 1; ++i){        y.at(i) = x.at(i + 1) - x.at(i);    }    std::sort(y.begin(), y.end());    int ans = 0;    if(n < m){        for(int i = 0; i < m - n; ++i){            ans += y.at(i);        }    }else{        ans = 0;    }    std::cout << ans << std::endl;    return 0;}