#include <iostream>#include <vector>int main(){    int n, m;    std::cin >> n >> m;    std::vector<int> x(m);    for(int i = 0; i < m; ++i){        std::cin >> x.at(i);    }    std::multipam<int, std::pair<int, int>> span;    for(int i = 0; i < n - 1; ++i){        span.insert(std::make_pair(x.at(i + 1) - x.at(i), std::make_pair(i, i + 1)));    }}