#include <iostream>#include <vector>int main(){    int n;    std::cin >> n;    std::vector<int> a(n);    for(int i = 0; i < n; ++i){        std::cin >> a.at(i);    }    bool flg = true;    int min;    while(flg){        std::sort(a.begin(), a.end());        a.erase(std::unique(a.begin(), a.end()), a.end());                auto itr = std::find_if(a.begin(), a.end(), [](int i){            return 0 < i;        });        min = *itr;        for(auto jtr = itr + 1; a.end() != jtr; ++jtr){            *jtr %= min;        }        flg = !(1 == a.size() || 2 == a.size() && 0 == a.at(0));    }    std::cout << min << std::endl;    return 0;}