#include <iostream>#include <string>int main(){    std::string s;    std::cin >> s;    int red = std::count(s.begin(), s.end(), '0');    int blue = std::count(s.begin(), s.end(), '1');    if(1 == s.size()){        std::cout << 0 << std::endl;    }else if(red <= blue){        std::cout << red * 2 << std::endl;    }else{        std::cout << blue * 2 << std::endl;    }    return 0;}