#include <iostream>#include <map>int main(){    int n, m;    std::cin >> n >> m;    std::multimap<long, long> stores;    for(int i = 0; i < n; ++i){        long a, b;        std::cin >> a >> b;        stores.insert(std::make_pair(a, b));    }    std::multimap<long, long>::iterator itr = stores.begin();    long sum = 0, amount = 0;    while(sum < m){        while(sum < m && 0 < itr->second){            ++sum;            amount += itr->first;            --(itr->second);        }        ++itr;    }    std::cout << amount << std::endl;    return 0;}