#include <iostream>#include <vector>int main(){    long long n;    std::cin >> n;        std::vector<long long> vec(5);    for(int i = 0; i < 5; ++i){        std::cin >> vec.at(i);    }    long long min = *std::min_element(vec.begin(), vec.end()), ans = 5;    if(1 <= n / min){        if(0 == n % min){            ans += n / min - 1;        }else{            ans += n / min;        }    }    std::cout << ans << std::endl;    return 0;}