#include <iostream>#include <boost/format.hpp>#include <algorithm>#include <string>int main(){    std::string s;    std::cin >> s;    int n = s.size();        std::string t(1, '0'), u(1, '1');    for(int i = 1; i < n; ++i){        t += std::to_string((t.at(i - 1) + 1) % 2);        u += std::to_string((u.at(i - 1) + 1) % 2);    }    int tCount = 0, uCount = 0;    for(int i = 0; i < n; ++i){        if(s.at(i) != t.at(i)){            ++tCount;        }        if(s.at(i) != u.at(i)){            ++uCount;        }    }    std::cout << std::min(tCount, uCount) << std::endl;    return 0;}