#include <iostream>#include <vector>#include <algorithm>int main(){    int n;    std::cin >> n;    std::vector<int> d(n);    for(int i = 0; i < n; ++i){        std::cin >> d.at(i);    }    std::sort(d.begin(), d.end());    int sz = d.size(), ans = 0;    if(0 == sz % 2){        ans = d.at(sz/2) - d.at(sz/2 - 1);    }    std::cout << ans << std::endl;    return 0;}