#include <iostream>#include <cmath>int main(){    long long l, r;    std::cin >> l >> r;    long long min = 2 * std::pow(10, 9);    if(r - l + 1 < 2018){        for(long long i = l; i < r; ++i){            for(long long j = i + 1; j <= r; ++j){                min = std::min(min, (i * j) % 2019);            }        }    }else{        min = 0;    }    std::cout << min << std::endl;    return 0;}