#include <iostream>#include <cmath>#include <map>int main(){    int n;    std::cin >> n;    std::multimap<int, int> a;    int val;    for(int i = 0; i < n; ++i){        std::cin >> val;        a.insert(std::make_pair(val, i));    }    int max = a.rbegin()->first;    for(int i = 0; i < n; ++i){        int ans = max;        if(a.find(max)->second == i){            std::map<int, int>::reverse_iterator rit = a.rbegin();            ++rit;            ans = rit->first;        }                    std::cout << ans << std::endl;    }    return 0;}