#include <iostream>#include <numeric>#include <vector>int main(){    long long n;    std::cin >> n;    std::vector<long long> a(n + 1);    for(long long i = 0; i < n + 1; ++i){        std::cin >> a.at(i);    }    long long preSum = std::accumulate(a.begin(), a.end(), 0LL);    long long b;    for(long long i = 0; i < n; ++i){        std::cin >> b;        long long min = std::min(a.at(i), b);        b -= min;        a.at(i) -= min;        a.at(i + 1) -= std::min(a.at(i + 1), b);    }        std::cout << preSum - std::accumulate(a.begin(), a.end(), 0LL) << std::endl;        return 0;}