#include <iostream>#include <numeric>#include <vector>int main(){    int n;    std::cin >> n;    std::vector<int> a(n + 1), b(n);    for(int i = 0; i < n + 1; ++i){        std::cin >> a.at(i);    }    int sum = std::accumulate(a.begin(), a.end(), 0);    for(int i = 0; i < n; ++i){        std::cin >> b.at(i);    }        for(int i = n - 1; 0 <=i ; --i){        int min = std::min(a.at(i + 1), b.at(i));        b.at(i) -= min;        a.at(i + 1) -= min;        a.at(i) -= std::min(a.at(i), b.at(i));    }    std::cout << sum - std::accumulate(a.begin(), a.end(), 0) << std::endl;        return 0;}