#include <iostream>#include <vector>int main(){    int n;    std::cin >> n;    std::vector<int> h(n);    for(int i = 0; i < n; ++i){        std::cin >> h.at(i);    }        std::vector<int> vari(n - 1);    for(int i = n - 1; i > 0; --i){        if(h.at(i) < h.at(i - 1)){            h.at(i - 1) -= 1;            vari.at(i - 1) = h.at(i) - h.at(i - 1);        }    }    std::string ans = "Yes";    if(0 < std::count_if(vari.begin(), vari.end(), [](int a){return a < 0;})){        ans = "No";    }    std::cout << ans << std::endl;    return 0;}