#include <iostream>#include <cmath>int main(){    int n;    std::cin >> n;    int count = 0, curr = 0, next, ans = 0;    while(std::cin >> next){        if(next <= curr){            ++count;            ans = std::max(ans, count);        }else{            count = 0;        }        curr = next;    }    std::cout << ans << std::endl;    return 0;}