#include <iostream>#include <numeric>#include <vector>#include <cmath>int main(){    int n;    std::cin >> n;    std::vector<int> a(n), b(n - 1);    for(int i = 0; i < n - 1; ++i){        std::cin >> b.at(i);    }    a.at(0) = b.at(0);    a.at(n - 1) = b.at(n - 2);    for(int i = 1; i < n - 1; ++i){        a.at(i) = std::min(b.at(i - 1), b.at(i));    }    std::cout << std::accumulate(a.begin(), a.end(), 0);    return 0;}