#include <iostream>#include <vector>int main(){    int n, k, q;    std::cin >> n >> k >> q;    std::vector<int> reduction(n, 0);    int val;    while(std::cin >> val){        ++reduction.at(val - 1);    }    for(int i = 0; i < n; ++i){        if(k - q + reduction.at(i) <= 0){            std::cout << "No" << std::endl;        }else{            std::cout << "Yes" << std::endl;        }    }    return 0;}