#include <iostream>#include <vector>#include <map>bool compare(std::pair<int, int> a, std::pair<int, int> b){    return a.second < b.second;}int main(){    int n;    std::cin >> n;        std::vector<std::pair<int, int>> list(n);    int index = 1, num;    for(int i = 0; i < n; ++i){        std::cin >> num;        list.at(i) = std::make_pair(index++, num);    }    std::sort(list.begin(), list.end(), compare);    for(int i = 0; i < n; ++i){        std::cout << list.at(i).first;        if(list.size() - 1 == i){            std::cout << std::endl;        }else{            std::cout << " ";        }    }    return 0;}