#include <iostream>#include <algorithm>#include <vector>#include <cmath>int main(){    long long n;    std::cin >> n;    long long i = 1;    std::vector<long long> manhattans;    while(i <= std::sqrt(n)){        if(0 == n % i){            manhattans.push_back(i + n / i);        }        ++i;    }    std::cout << *std::min_element(manhattans.begin(), manhattans.end()) - 2 << std::endl;    return 0;}