#include <iostream>#include <set>int main(){    int n, m;    std::cin >> n >> m;    std::multiset<double> a;    for(int i = 0; i < n; ++i){        double val;        std::cin >> val;        a.insert(val);    }    for(int i = 0; i < m; ++i){        auto itr = a.end();        --itr;        a.insert(*itr / 2);        a.erase(itr);    }        long long ans = 0;    for(auto itr = a.begin(); a.end() != itr; ++itr){        ans += *itr;    }    std::cout << ans << std::endl;    return 0;}