#include <iostream>#include <vector>long long countPrime(long long readVal){    long long write = 0;    if(2 == readVal){        ++write;    }    if(0 != readVal % 2){        for(int i = 3; i * i <= readVal; ++i){            if(0 != readVal % i){                ++write;            }        }    }    return write;}long long gcd(long long a, long long b){    long long max = std::max(a, b), min = std::min(a, b);    if(0 == max % min){        return min;    }else{        return gcd(min, max % min);    }}int main(){    long long a, b;    std::cin >> a >> b;    std::cout << countPrime(gcd(a, b)) + 1 << std::endl;    return 0;}