#include <iostream>#include <iomanip>#define _USE_MATH_DEFINES#include <cmath>int main(){    double a, b, x;    std::cin >> a >> b >> x;    double ref = a * b / 2, filled = x / a, ans = 0;    if(filled <= ref){        ans = M_PI / 2 - std::atan2(2 * filled, std::pow(b, 2));    }else{        ans = std::atan2(2 * (a * b - filled), std::pow(a, 2));    }        std::cout << std::fixed << std::setprecision(7) << ans / M_PI * 180 << std::endl;    return 0;}