#include <iostream>#include <vector>bool isDeadEnd(std::vector<std::string>& readVec, int r, int c){    int count = 0;    for(int i = -1; i <= 1; ++i){        for(int j = -1; j <= 1; ++j){            if(0 != i && 0 != j && '.' == readVec.at(r + i).at(c + j)){                ++count;            }        }    }    bool write = false;    if(1 == count){        write = true;    }    return write;}int main(){    int h, w;    std::cin >> h >> w;    std::vector<std::string> maze(h + 2, std::string(w + 2, '#'));    for(int i = 1; i <= h; ++i){        for(int j = 1; j <= w; ++j){            std::cin >> maze.at(i).at(j);        }    }    return 0;}