#include <iostream>int main(){    std::string s, t;    std::cin >> s >> t;    std::vector<std::vector<int>> dp(s.size() + 1, std::vector<int>(t.size() + 1, 0));    for(int i = 0; i < t.size(); ++i){        for(int j = 0; j < s.size(); ++j){            if(s.at(j) == t.at(i)){                dp.at(j + 1).at(i + 1) = dp.at(j).at(i) + 1;            }            dp.at(j + 1).at(i) = dp.at(j).at(i);            dp.at(j + 1).at(i) = dp.at(j).at(i);        }    }    }