#include <iostream>int main(){    std::string day;    std::cin >> day;    int ans = 0;    if("Monday" == day){        ans = 5;    }else if("Tuesday" == day){        ans = 4;    }else if("Wednesday" == day){        ans = 3;    }else if("Thursday" == day){        ans = 2;    }else if("Friday" == day){        ans = 1;    }    std::cout << ans << std::endl;    return 0;}