#include <iostream>#include <iomanip>#include <vector>#include <algorithm>#include <cmath>int main(){    int n;    std::cin >> n;    std::vector<std::vector<int>> pos(n, std::vector<int>(2));    for(int i = 0; i < n; ++i){        std::cin >> pos.at(i).at(0) >> pos.at(1).at(1);    }    double max = 0;    for(int i = 0; i < n; ++i){        for(int j = i; j < n; ++j){            double xVari = pos.at(i).at(0) - pos.at(j).at(0);            double yVari = pos.at(i).at(1) - pos.at(j).at(1);            max = std::max(max, std::sqrt(std::pow(xVari, 2) + std::pow(yVari, 2)));        }    }    std::cout << std::fixed << std::setprecision(4) << max << std::endl;    return 0;}