#include<iostream>#include<vector>int main(){    int n;    std::cin >> n;        std::vector<int> counts(6, 0);    for(int i = 0; i < n; ++i){        double max, min;        std::cin >> max >> min;        if(35 <= max){            ++counts.at(0);        }else if(30 <= max){            ++counts.at(1);        }else if(25 <= max){            ++counts.at(2);        }        if(25 <= min){            ++counts.at(3);        }                if(min < 0 && 0 <= max){            ++counts.at(4);        }                if(max < 0){            ++counts.at(5);        }    }    for(int i = 0; i < 6; ++i){        std::cout << counts.at(i);        if(5 != i){            std::cout << " ";        }else{            std::cout << std::endl;        }    }    return 0;}