#include <iostream>#include <vector>const int INF = 1e9;void print(std::vector<int> readVec){    int sz = readVec.size();    for(int i = 0; i < sz; ++i){        std::cout << readVec.at(i);        if(sz - 1 == i){            std::cout << " ";        }else{            std::cout << std::endl;;        }    }}int main(){    int n;    std::cin >> n;    std::vector<int> h(n);    for(int i = 0; i < n; ++i){        std::cin >> h.at(i);    }    std::vector<int> dp(n, INF);    dp.at(0) = 0;    dp.at(1) = std::abs(h.at(1) - h.at(0));    for(int i = 2; i < n; ++i){        int first = dp.at(i - 2) + std::abs(h.at(i) - h.at(i - 2));        int second = dp.at(i - 1) + std::abs(h.at(i) - h.at(i - 1));        dp.at(i) = std::min(first, second);    }    std::cout << dp.at(n - 1) << std::endl;    return 0;}