#include <iostream>#include <vector>const int INF = 1e9;void print(std::vector<int> readVec){    int sz = readVec.size();    std::cout << "---" << std::endl;    for(int i = 0; i < sz; ++i){        std::cout << readVec.at(i) << std::endl;    }}int main(){    int n, k;    std::cin >> n >> k;    std::vector<int> h(n);    for(int i = 0; i < n; ++i){        std::cin >> h.at(i);    }    std::vector<int> dp(n, INF);    dp.at(0) = 0;    dp.at(1) = std::abs(h.at(1) - h.at(0));    for(int i = 2; i < n; ++i){        int limit = std::min(i, k);        for(int j = 1; j <= limit; ++j){            dp.at(i) = std::min(dp.at(i), dp.at(i - j) + std::abs(h.at(i) - h.at(i - j)));        }    }    std::cout << dp.at(n - 1) << std::endl;    return 0;}