#include <iostream>#include <vector>const int inf = 1e9;void print(std::vector<int> readvec){    int sz = readvec.size();    std::cout << "---" << std::endl;    for(int i = 0; i < sz; ++i){        std::cout << readvec.at(i) << std::endl;    }}int main(){    int n, w_limit;    std::cin >> n >> w_limit;    std::vector<int> w(n), v(n);    for(int i = 0; i < n; ++i){        std::cin >> w.at(i) >> v.at(i);    }    std::vector<std::vector<long>> dp(n + 1, std::vector<long> (w_limit + 1, 0));    for(int i = 0; i < n; ++i){        for(int j = 0; j <= w_limit; ++j){            long candidate;            if(w.at(i) <= j){                candidate = dp.at(i).at(j - w.at(i)) + v.at(i);            }else{                candidate = dp.at(i).at(j);            }            dp.at(i + 1).at(j) = std::max(dp.at(i).at(j), candidate);        }    }    std::cout << *std::max_element(dp.at(n).begin(), dp.at(n).end()) << std::endl;    return 0;}