#include <iostream>int main(){    std::string s;    std::cin >> s;    std::string ref = "keyence", ans = "NO";        int sz = ref.size();    for(int i = 0; i < sz; ++i){        int pos =  s.find(ref.substr(0, i));        if(std::string::npos != pos){            int head = pos + i;            std::string t = s.substr(head, s.size() - head);            if(std::string::npos != t.find(ref.substr(i, sz - i))){                ans = "YES";                break;            }        }    }    std::cout << ans << std::endl;    return 0;}