#include <algorithm>#include <iostream>#include <vector>#include <cmath>#define INF 100000000000000007int main(){    int n;    std::cin >> n;    std::vector<long long> a(n), b(n);    for(int i = 0; i < n; ++i){        std::cin >> a.at(i) >> b.at(i);    }    long long ans = INF;        for(int i = 0; i < n; ++i){ // entrance        for(int j = 0; j < n; ++j){ // exit            long long sum = 0;            for(int k = 0; k < n; ++k){ // person                long long min = std::min(a.at(i), b.at(j));                long long max = a.at(i) + b.at(j) - min;                sum += std::abs(a.at(k) - min);                sum += std::abs(b.at(k) - a.at(k));                sum += std::abs(max - b.at(k));            }            ans = std::min(ans, sum);        }    }    std::cout << ans << std::endl;    return 0;}